const initState={
    authError:null
}

const authReducer = (state = initState,action) =>{
    console.log(action);

    switch(action.type){
        case 'LOGIN_SUCCESS':
            return {
                ...state,
                authError:'Login failed'
        };
        case 'LOGIN_ERROR':
            return {
                ...state,
                authError:null
            };            
        default:
            return state;            
    }
}

export default authReducer;
