const mysql = require('mysql');
export const connection = mysql.createConnection({
        host: 'localhost',
        user: 'user',
        password: 'password',
        database: 'database name'
    });
    connection.connect((err) => {
        if (err) throw err;
        console.log('Connected!');
    });
